    ui.fourPage.backgroundDrawable = GradientDrawable("TL_BR", ["#80DEEA", "#0097A7", "#0097A7"]);//浅蓝色
    ui.skip.click(() => ui.viewpager.currentItem = ui.viewpager.childCount);//点击跳过则跳转到最后一页
