/**
 * 检测版本信息
 */
function initializeUpgradeInfo() {
    try {
        if (isLogin) {
            try {
                threads.start(function () {
                    let android_id = device.getAndroidId();
                    var url_address = rootUrl + "/App/CheckSoftVersionInfo?deviceId=" + android_id + "&softCode=dean&softVersion=" + softVersion + "&softType=1";
                    //toastError(url_address)
                    var response = http.get(url_address);
                    if (response.statusCode == 200) {
                        var json = response.body.json();
                        if (json.success == "true") {
                            if (json.upgrade_code == "0") {
                                toastInfo(json.message)
                            } else if (json.upgrade_code == "1") {
                                dialogs.build({
                                    title: "发现新版本",
                                    positive: "升级",
                                    neutral: "取消",
                                    items: ["发现新版本，是否升级？点击【升级】进行升级，点击【取消】不进行升级.切记使用浏览器下载请勿使用应用市场."]
                                })
                                    .on("positive", () => {
                                        app.openUrl(rootUrl + json.data);
                                    })
                                    .on("show", (dialog) => { }).show();
                            } else {
                                toastError("请求远端服务器出现异常！请稍后重试！");
                            }
                        } else {
                            toastInfo("检查版本出现问题"+json.message)
                        }
                    } else if (response.statusCode == 404) {
                        toastError("检查版本出现问题访问服务器出现错误404错误请稍后重试")
                    }
                    else {
                        toastError("检查版本出现问题无法连接服务器")
                    }
                });
            } catch (error) {

            }
        }
    } catch (error) {

    }
}
