function autoChain(appNameList, isShowConsole, IsAutoSign, IsAutoComment, IsClearCache, IsCashOut,
    timesInterval, probability, isFragmentation, fragmentationTime, CurveBrushScreen, WateForTime, IsCloseApp,
    IsKuaiShouSilder, IsShowKeepOnlineApp, IsBackMusic, IsChangeM) {
    device.keepScreenDim();//保持屏幕常亮
    device.setMusicVolume(0)//设置系统声音
    /**
    * 是否开启控制台，不建议开启偶尔会遮挡click事件
    */
    threads.start(function () {
        if (isShowConsole) {
            console.show();
            console.setPosition(0, device.height / 3);
            console.setSize(device.width, device.height / 4);
        }
    });

    var execAutoBrushDate = getDate();
    while (true) {
        if (checkTimesIsZeroTime(execAutoBrushDate, 1)) {
            toastWarn("第一层今日薅羊毛结束执行下一天任务...");
            continue;//说明是第二天的零点应该重新执行循环
        }
        backMusicExecute(IsBackMusic, timesInterval);
        for (let x = 0; x < appNameList.length; x++) {
            if (checkTimesIsZeroTime(execAutoBrushDate, 2)) {
                toastWarn("第二层今日薅羊毛结束执行下一天任务...");
                continue;//说明是第二天的零点应该重新执行循环
            }
            let appName = appNameList[x];
            let execTimes = havedChainTimes.get(appName);//app执行时间
            let see_second = execTimes * 60;//转换成秒
            let executeCount = parseInt(see_second / parseInt(timesInterval));//分钟转换成秒再除以每次的时间间隔。
            let exectue_status = computerExctueTime(appName, execTimes, isFragmentation, fragmentationTime)
            if (exectue_status == 2) {
                toastInfo(appName + "时间已到，因此跳过");
                continue;//今日时间已到 执行下一个app
            }
            app.launchApp(appName);//启动App
            toastInfo("当前自动阅读程序" + appName + "等待进入");//系统日志提示
            cutDownBySleep(WateForTime)//启动App时候等待时间
            adolescentWindows();//关闭青少年窗口
            closeUpgradetWindows();//关闭升级提示框
            computerTrainTime(); //试用版
            if (appName == "凹音短视频") {
                凹音短视频(appName, executeCount, execTimes, timesInterval, IsAutoSign, IsAutoComment, IsClearCache, IsCashOut, probability, execAutoBrushDate, CurveBrushScreen, IsChangeM, IsCloseApp, isFragmentation, fragmentationTime);
                if (IsCloseApp)
                    stopApp(appName);
            } else if (appName == "秘乐短视频") {
                秘乐短视频(appName, executeCount, execTimes, timesInterval, IsAutoSign, IsAutoComment, IsClearCache, IsCashOut, probability, execAutoBrushDate, CurveBrushScreen, IsCloseApp, isFragmentation, fragmentationTime);
                if (IsCloseApp) {
                    stopApp(appName);
                }
            } else if (appName == "鲜绿有品") {
                鲜绿有品(appName, executeCount, execTimes, timesInterval, IsAutoSign, IsAutoComment, IsClearCache, IsCashOut, probability, execAutoBrushDate, CurveBrushScreen, IsCloseApp, isFragmentation, fragmentationTime);
                if (IsCloseApp) {
                    stopApp(appName);
                }
            } else if (appName == "蚂蚁短视频") {
                蚂蚁短视频(appName, executeCount, execTimes, timesInterval, IsAutoSign, IsAutoComment, IsClearCache, IsCashOut, probability, execAutoBrushDate, CurveBrushScreen, IsCloseApp, isFragmentation, fragmentationTime);
                if (IsCloseApp) {
                    stopApp(appName);
                }
            } else if (appName == "本色世界") {
                本色世界(appName, executeCount, execTimes, timesInterval, IsAutoSign, IsAutoComment, IsClearCache, IsCashOut, probability, execAutoBrushDate, CurveBrushScreen, IsCloseApp, isFragmentation, fragmentationTime);
                if (IsCloseApp) {
                    stopApp(appName);
                }
            } else if (appName == "好玩吧") {
                好玩吧(appName, executeCount, execTimes, timesInterval, IsAutoSign, IsAutoComment, IsClearCache, IsCashOut, probability, execAutoBrushDate, CurveBrushScreen, IsCloseApp, isFragmentation, fragmentationTime);
                if (IsCloseApp) {
                    stopApp(appName);
                }
            } else if (appName == "顶点号") {
                顶点号(appName, executeCount, execTimes, timesInterval, IsAutoSign, IsAutoComment, IsClearCache, IsCashOut, probability, execAutoBrushDate, CurveBrushScreen, IsCloseApp, isFragmentation, fragmentationTime);
                if (IsCloseApp) {
                    stopApp(appName);
                }
            }
            //好玩吧 顶点号
        }
    }
}

