        function getsd(s, ary) {
            //将数组内所有值的平方和开方等于s
            var sum = weiyi(ary);
            var S = (s / sum) || 0;
            for (var i = 0; i < ary.length; i++) {
                ary[i] = ary[i] * S;
            };
            return ary;
        };

        function weiyi(ary) {
            //数组所有值平方和开方
            var sum = 0;
            for (var i = 0; i < ary.length; i++) {
                sum += Math.pow(ary[i], 2);
            };
            return Math.sqrt(sum);
        };

