/**
 * 集群在线心跳包
 */
function initializeClusterInfo() {

    let IsUseCluster = woolStorage.get("IsUseCluster");
    let serviceUrl = woolStorage.get("serviceUrl");
    let userProfile = woolStorage.get("userProfileName");

    if (serviceUrl == undefined || serviceUrl == "") {
        return
    }
    if (IsUseCluster == "false") {
        if (HeartBeatAndTaskerTimer != null) {
            clearInterval(HeartBeatAndTaskerTimer)
        }
        if (heartThread != null) {
            heartThread.interrupt()
        }
    }
    if (String(IsUseCluster).toLowerCase() == "true") {

        heartThread = threads.start(function () {
            try {
                toastLog("一次性开启心跳访问线程...")
                var ClusterService = woolStorage.get("ClusterService"); //集群监听和任务服务器
                var HeartBeatTimes = 60
                if (woolStorage.get("HeartBeatTimes") == null) {

                } else {
                    HeartBeatTimes = parseInt(woolStorage.get("HeartBeatTimes")); //心跳访问服务器间隔
                }
                if (String(IsUseCluster).toLowerCase() == "true") {
                    executeTask(ClusterService) //立即执行一次
                }
                HeartBeatAndTaskerTimer = setInterval(() => {
                    device.keepScreenOn(3600 * 10)
                    if (!device.isScreenOn()) {
                        device.wakeUp();
                    }
                    //toastWarn("定时器任务被执行~")
                    if (String(IsUseCluster).toLowerCase() == "true") {
                        executeTask(ClusterService)
                    }
                }, HeartBeatTimes * 1000);
            } catch (error) {
                //toastError("云控心跳发生错误" + error) //停止报错提现
                if (HeartBeatAndTaskerTimer != null) {
                    clearInterval(HeartBeatAndTaskerTimer)
                }
                if (heartThread != null) {
                    heartThread.interrupt()
                }
            }
        });
    }
}
/**
 * 执行云控任务
 * @param {云控服务地址} ClusterService 
 */
function executeTask(ClusterService) {
    let serviceUrl = woolStorage.get("serviceUrl");
    let userProfile = woolStorage.get("userProfileName");
    let rootUrl = woolStorage.get("rootUrl");//顶级域名
    if (rootUrl == "") {
        toastWarn("服务器根地址不正确心跳访问出现错误")
        return
    }
    let deviceName = device.brand + " " + device.model
    let ip_address_gps = getIpAdressAndIpGps().split("@")
    let url_address = rootUrl + "/App/HeartBeat?deviceId=" + device.getAndroidId() + "&deviceName=" + deviceName + "&deviceType=android&softName=迪恩&softCode=dean&gps=" + ip_address_gps[1] + "&ip=" + ip_address_gps[0] + "&ipAddress=" + ip_address_gps[1] + "";
    try {
        var response = http.get(url_address);
        if (response.statusCode == 200) {
            var json = response.body.json();
            if (json.success == "true") {
                if (json.havetask == "true") {
                    let configJson = null;
                    if (json.serverConfig != null) {
                        configJson = JSON.parse(json.serverConfig)
                    }
                    let taskJson = JSON.parse(json.data)
                    let taskLength = taskJson.length
                    //将任务全部变化成已执行
                    if (taskLength > 0) {
                        //任务状态true是单任务False是多任务 多任务也好开启线程监听
                        var IsSingleTask = woolStorage.get("IsSingleTask");
                        // if(IsSingleTask==undefined){
                        //     toastError("未定义")
                        // }else{
                        //     if (IsSingleTask == "true") {
                        //         toastError("未定义"+IsSingleTask)
                        //     }
                        //     toastLog(IsSingleTask) 
                        // }
                        //更改状态
                        for (let i = 0; i < taskLength; i++) {
                            let taskCode = taskJson[i].APP_TASK_CODE //任务编码
                            let url = ClusterService + "/App/UpdateDeviceTask?deviceId=" + device.getAndroidId() + "&taskStatus=2&taskCode=" + taskCode + "";
                            var response = http.get(url);
                            if (response.statusCode == 200) { }
                            clusterTaskList.push(taskJson[i])
                        }

                        //单一任务模式,获取服务器最新的就可以了
                        if (IsSingleTask == "true") {
                            let taskCode = taskJson[0].APP_TASK_CODE //任务编码
                            let taskTime = taskJson[0].APP_TASK_TIME //任务时间
                            let taskName = taskCode + "Thread";
                            if (clusterThread != null) {
                                clusterThread.interrupt();//停止线程
                                toastInfo("停止上一个线程成功！");
                            }
                            clusterThread = threads.start(function () {
                                taskCenter(taskCode, taskTime, configJson)
                            });
                        } else {
                            if (monitoTaskThread == null) {
                                //线程里面出线程
                                monitoTaskThread = threads.start(function () {
                                    //单个任务的时候有的区分一下 否则会出问题
                                    for (let i = 0; i < clusterTaskList.length; i++) {
                                        //线程未开启则不进行等待，开启后则进行等待
                                        if (clusterThread != null) {
                                            while (previousTaskStatus) {
                                                sleepRandom1(); //1秒响应一次任务是否完成
                                            }
                                            toastInfo("停止上一个线程成功！");
                                        }
                                        let taskCode = clusterTaskList[i].APP_TASK_CODE //任务编码
                                        let taskTime = clusterTaskList[i].APP_TASK_TIME //任务时间
                                        clusterThread = threads.start(function () {
                                            taskCenter(taskCode, taskTime, configJson) //线程里面执行任务
                                        });
                                    }
                                });
                            }
                        }

                    } else {
                        //没有任务    
                        toastWarn("理论值进不来，没有任务")
                    }
                } else {
                    //没有任务
                    toastWarn("服务器反馈：" + json.message)
                    return
                }
            } else {
                toastError("服务器错误信息，要求终止心态线程" + json.message)
                if (HeartBeatAndTaskerTimer != null) {
                    clearInterval(HeartBeatAndTaskerTimer)
                }
            }
        } else if (response.statusCode == 404) {
            toastError("404集群在线心跳包访问服务器出现错误")
            if (HeartBeatAndTaskerTimer != null) {
                clearInterval(HeartBeatAndTaskerTimer)
            }
        } else {
            toast("集群在线心跳包无法连接服务器")
            if (HeartBeatAndTaskerTimer != null) {
                clearInterval(HeartBeatAndTaskerTimer)
            }
        }
    } catch (error) {
        toastError("本次请求失败放弃...错误的原因大致是：" + error)
    }
}
