function checkDeviceIsEffect() {
    /**
    * 是否开启控制台，不建议开启偶尔会遮挡click事件
    */
    testThread = threads.start(function () {
        let url_address = rootUrl + "/App/GetDeviceIsRegister?deviceId=" + device.getAndroidId();
        var response = http.get(url_address);
        if (response.statusCode == 200) {
            var json = response.body.json();
            if (json.success == "true") {
                return true;
            } else {
                toastError(json.message)
                return false;
            }
        }
    });
}
