    function recordExecuteCount(appName, businessName) {
        let key = appName + businessName + storageSign + getDate();

        let value = 1;
        let havedExectueCount = woolStorage.get("" + key + ""); //系统存储已经执行的时间
        if (havedExectueCount == null) {
            woolStorage.put(key, value);
        } else {
            value = parseInt(1) + parseInt(havedExectueCount);
            woolStorage.put(key, value);
        }
    }
    /**
     * 获取执行次数
     * @param {软件名} appName 
     * @param {业务名} businessName 
     */
    function getExectueCount(appName, businessName) {
        let key = appName + businessName + storageSign + getDate();
        //woolStorage.remove(key)
        let havedExectueCount = woolStorage.get("" + key + ""); //系统存储已经执行的时间
        if (havedExectueCount == null) {
            return 0;
        } else {
            return parseInt(havedExectueCount);
        }
    }
