function findImageByRegion(imageName, searchRegion) {
    let foundImage = rootPath + softPath + screenshotPath + imageName

    toastLog(foundImage);
    if (files.exists(foundImage)) {
        var imagesLitle = images.read(foundImage)
        var result = findImage(captureScreen(), imagesLitle, { region: searchRegion, threshold: 0.8 });
        if (result) {
            return result
        } else {
            toastLog("图片未匹配到...")
            return null
        }
    } else {
        toastLog("图片不存在")
        return null
    }
}
