/**
 * 生成随机数
 * @param {最大随机数} num 
 * @param {随机数得数量} n 
 */
function randomNum(num, n) {
    if (typeof num !== "number" || typeof n !== "number") return false;  //对象检测
    var aNum = [];
    if (num <= n) {
        for (var j = 0; j < num; j++) {
            if (random != 0) {
                aNum.push(j);
            }
        };
        return aNum;
    }         //如果n大于num就生成0到num-1的每一个数
    else {
        while (aNum.length < n) {
            var random = Math.round(Math.random() * num);
            if (aNum.indexOf(random) == -1) {
                if (random != 0) {
                    aNum.push(random);
                }
            }
        }
        return aNum;
    }
}
